package com;

public class sumnatural {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int n = 10;  
        int sum = 0;
        for (int i = 1; i <= n; i++) {
            sum += i;
        }

        System.out.println("Sum of first " + n + " natural numbers: " + sum);


	}

}
